import React, {useState} from 'react';
import './App.css'; // Import your CSS file

function App() {
  const OrangeBorder = {
    height: '40vh', // Set the height to cover one-third of the viewport height
    backgroundColor: 'orange', // Change this to your desired color
  };



  const SearchBar = () => {
    const [data, setData] = useState()

    function getData(val) {
      setData(val.target.value)
    }

    function submitSearch() {
      setData("Hello")                // CHANGE THIS CODE
    }

    return (
      <div className = "searchBar">
        <h1> {data} </h1>
        <input type="text" onChange={getData}></input>
        <button onClick={submitSearch} width= '150px'> Match! </button>
      </div>
    );
  };

  return (
    <div className="App">
      {/* Your other content before the colored section */}
      <div className="styling" style={{ marginTop: '0', ...OrangeBorder }}></div>

      <SearchBar></SearchBar>

      {/* Your other content after the colored section */}
    </div>
  );
}

export default App;





