import React, {useState} from 'react';
import './App.css'; // Import your CSS file
import InputBox from './InputBox'

function App() {
  const OrangeBorder = {
    height: '40vh', // Set the height to cover one-third of the viewport height
    backgroundColor: 'orange', // Change this to your desired color
  };

  const SearchBar = ({ data }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState(data);

    const handleInputChange = (event) => {
      const value = event.target.value;
      setSearchTerm(value);

      const filteredData = data.filter((item) =>
        item.toLowerCase().includes(value.toLowerCase())
      );
      setSearchResults(filteredData);
    };

    return (
      <div>
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={handleInputChange}
        />
        <ul>
          {searchResults.map((result, index) => (
            <li key={index}>{result}</li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="App">
      {/* Your other content before the colored section */}
      <div className="styling" style={{ marginTop: '0', ...OrangeBorder }}></div>

      <SearchBar></SearchBar>

      {/* Your other content after the colored section */}
    </div>
  );
}

export default App;





