import React, {useState} from 'react';
import './App.css'; // Import your CSS file
import logo from './microphone.svg'

function App() {
  const OrangeBorder = {
    top: '0', 
    left: '0',
    width: '100%',
    height: '40vh',
    backgroundColor: 'orange',
    position: 'absolute', 
    marginTop: '0',
    zIndex: '-1'
  };

  const SearchBar = () => {
    const [data, setData] = useState()

    function getData(val) {
      setData(val.target.value)
    }

    function submitSearch() {
      setData("Hello")                // CHANGE THIS CODE
    }

    const textBoxStyle = {

    }

    return (
      <div class="form__group field">
        <input type="input" onChange={getData} class="form__field" placeholder="lyrics" name="lyrics-input" id='lyrics-input' required />
        <label for="lyrics-input" class="form__label">Lyrics:</label>
        {/* <h1> {data} </h1> */}
      </div>
    );
  };

  const Square = ({left, right, flex}) => {
    const squareStyle = {
      left: left,
      right: right,
      top: '55%',
      flex: flex,
    };

    return (
      <div className="square" style={squareStyle}>     
      </div>
    );
  };

  return (


    <div className="App">
      <div className="orange-border" style={{...OrangeBorder }}></div>
      <img src={logo} alt="Microphone Logo" style={{height: 50, width: 50, position: 'absolute', left: 0}}/>
      <div className="search-bar-container">
        <SearchBar></SearchBar>
      </div>
      <div className="albumns-container">
      <Square></Square>
      <Square></Square>
      <Square></Square>

      </div>

    </div>
  );
}

export default App;





