import React, {useState} from 'react';
import './App.css'; // Import your CSS file

function App() {
  const OrangeBorder = {
    height: '40vh', // Set the height to cover one-third of the viewport height
    backgroundColor: 'orange', // Change this to your desired color
  };



  const SearchBar = () => {
    const [data, setData] = useState()

    function getData(val) {
      setData(val.target.value)
    }

    function submitSearch() {
      setData("Hello")                // CHANGE THIS CODE
    }

    return (
      <div className = "searchBar">
        <h1> {data} </h1>
        <input type="text" onChange={getData} style= {{ width: '150px'}}></input>
        <button onClick={submitSearch} style= {{ width: '150px', height: '40px'}}> Match! </button>
      </div>
    );
  };

  const Square = ({left, right, top, bottom, flex, justifyContent}) => {
    const squareStyle = {
      left: left,
      right: right,
      top: top,
      bottom: bottom,
      flex: flex,
      justifyContent: justifyContent
    };

    return (
      <div className="square" style={squareStyle}>     
      </div>
    );
  };

  return (
    <div className="App">
      <div className="styling" style={{ marginTop: '0', ...OrangeBorder }}></div>

      <SearchBar></SearchBar>

      <div className="albumns-container" style={{display: 'flex'}}>
      <Square justifyContent="space-evenly"></Square>
      <Square></Square>
      <Square></Square>
      {/* <Square left="30%" bottom="0%"></Square>
      <Square left="70%" bottom="50%"></Square> */}
      </div>

    </div>
  );
}

export default App;





